import React from "react";
import styled from "styled-components";

const Button = (props) => {
    const {text, _onClick, is_float, children, margin, width, padding } = props;

    if (is_float) {
        return (
            <React.Fragment>
                <FloatButton onClick={_onClick}>{text? text : children}</FloatButton>
            </React.Fragment>
        );
    }
    const styles = {
        margin: margin,
        width: width,
        padding: padding,
      };

    return (
        <React.Fragment>
            <ElButton onClick={_onClick}>{text}</ElButton>
        </React.Fragment>
    );
};

Button.defaultProps = {
    text: "텍스트",
    _onClick: () => {},
    is_float: false,
};

const ElButton = styled.button`
    width: 100%;
    background-color: #212121;
    color: #ffffff;
    padding: 12px 0px;
    box-sizing: border-box;
    border: none;
`;

const FloatButton = styled.button`
    width: 60px;
    height: 60px;
    background-color: #212121;
    color: white;
    box-sizing: border-box;
    font-size: 30px;
    font-weight: 800;
    position: fixed;
    bottom: 50px;
    right: 16px;
    border: none;
    text-align: center;
    border-radius: 30px;
`;

export default Button;
