import { createAction, handleActions } from "redux-actions";
import { produce } from "immer";
// 불변성을 유지하기 위해서 immer사용
import { firestore, storage } from "../../shared/firebase";
import moment from "moment";

import { actionCreators as imageActions } from "./image";

// 1번순서)액션타입
const SET_POST = "SET_POST";
const ADD_POST = "ADD_POST";

// 2번순서)액션생성자
const setPost = createAction(SET_POST, (post_list) => ({ post_list }));
const addPost = createAction(ADD_POST, (post) => ({ post }));

// 3번순서)이니셜스테이트(리듀서가 사용할)
const initialState = {
  list: [],
};

const initialPost = {
  // id: 0,
  // user_info: {
  //     user_name: "mean0",
  //     user_profile:
  //         "https://www.allkpop.com/upload/2021/12/content/231225/web_data/allkpop_1640280755_untitled-1.jpg",
  // },
  image_url:
    "data:image/png;base64,",
  contents: "고양이네요!",
  comment_cnt: 0,
  insert_dt: moment().format("YYYY-MM-DD hh:mm:ss"),
};

const addPostFB = (contents = "") => {
  return function (dispatch, getState, { history }) {
    const postDB = firestore.collection("post");

    const _user = getState().user.user;
    console.log(_user);
    const user_info = {
      user_name: _user.user_name,
      user_id: _user.uid,
      user_profile: _user.user_profile,
    };

    console.log(user_info);
    const _post = {
      ...initialPost,
      contents: contents,
      insert_dt: moment().format("YYYY-MM-DD hh:mm:ss"),
    };

    const _image = getState().image.preview;
    console.log(typeof _image);
    console.log(_image);

    const _upload = storage
      .ref(`images/${user_info.user_id}_${new Date().getTime()}`)
      .putString(_image, "data_url");

    _upload.then((snapshot) => {
      snapshot.ref
        .getDownloadURL()
        .then((url) => {
          console.log(url);
          return url;
        })
        .then((url) => {
          postDB
            .add({ ...user_info, ..._post, image_url: url })
            .then((doc) => {
              let post = {
                user_info,
                ..._post,
                id: doc.id,
                image_url: url,
              };
              console.log(post);
              dispatch(addPost(post));
              history.replace("/");
              dispatch(imageActions.setPreview(null));
            })
            .catch((err) => {
              window.alert("앗! 포스트 작성에 문제가 있어요!");
              console.log("post 작성 실패!", err);
            });
        })
        .catch((err) => {
          window.alert("앗! 이미지 없로드에 문제가 있어요!");
          console.log("앗! 이미지 업로드에 문제가 있어요!", err);
        });
    });
  };
};
    // &1)firebase 사이트에서 post컬렉션만든후 
//&2)firebase에서 포스트 가져오기
const getPostFB = () => {
  return function (dispatch, getState, { history }) {
    // firebase db에 접근하기
    const postDB = firestore.collection("post");
    // 그다음 데이터 전부 가져오기
    postDB.get().then((docs) => {
        // 가져온다음
      let post_list = [];

      docs.forEach((doc) => {
        // firestore에서 가지고 온 값들을 _post에
        let _post = doc.data();
        

        //["commenct_cnt", "content", ...](딕셔너리의 키값들로 배열을 만들어주어라)
        let post = Object.keys(_post).reduce(
          (acc, cur) => {
            //   3-3강의
            if (cur.indexOf("user_") !== -1) {
              return {
                ...acc,
                user_info: {
                  ...acc.user_info,
                  [cur]: _post[cur],
                },
              };
            }
            return { ...acc, [cur]: _post[cur] };
          },
          {
            id: doc.id,
            user_info: {},
          }
        );
        post_list.push(post);
      });
      console.log(post_list);
      dispatch(setPost(post_list));
    });
  };
};

// 4번순서)리듀서만들기
export default handleActions(
  {
    [SET_POST]: (state, action) =>
      // 어떤처리를 해줄지 적어주기
      produce(state, (draft) => {
        // 위에꺼가지고 어떤처리할지
        draft.list = action.payload.post_list;
      }),

    [ADD_POST]: (state, action) =>
      produce(state, (draft) => {
        // unshift는 배열 맨 앞에 데이터를 넣어줘요!
        draft.list.unshift(action.payload.post);
      }),
  },
  initialState
);

// &3)export하기
const actionCreators = {
  setPost,
  addPost,
  getPostFB,
  addPostFB,
};

export { actionCreators };
