import { createAction, handleActions } from "redux-actions";
import { produce } from "immer";
// 불변성을 유지하기 위해서 immer사용
import { firestore, storage } from "../../shared/firebase";
import moment from "moment";

import { actionCreators as imageActions } from "./image";
import { queryByTestId } from "@testing-library/react";

// 1번순서)액션타입
const SET_POST = "SET_POST";
const ADD_POST = "ADD_POST";
const EDIT_POST = "EDIT_POST";
//페이지로딩중인지 판별
const LOADING = "LOADING";

// 2번순서)액션생성자
const setPost = createAction(SET_POST, (post_list, paging) => ({
  post_list,
  paging,
}));
const addPost = createAction(ADD_POST, (post) => ({ post }));
const editPost = createAction(EDIT_POST, (post_id, post) => ({
  post_id,
  post,
}));
const loading = createAction(LOADING, (is_loading) => ({ is_loading }));

// 3번순서)이니셜스테이트(리듀서가 사용할)
const initialState = {
  list: [],
  // 시작점,다음리스트있는지여부,몇개가져올지
  paging: { start: null, next: null, size: 3 },
  // 로딩중인지 가져오고있는중인지
  is_loadging: false,
};

const initialPost = {
  // id: 0,
  // user_info: {
  //     user_name: "mean0",
  //     user_profile:
  //         "https://www.allkpop.com/upload/2021/12/content/231225/web_data/allkpop_1640280755_untitled-1.jpg",
  // },
  image_url:
    "data:image/png;base64,",
  contents: "고양이네요!",
  comment_cnt: 0,
  insert_dt: moment().format("YYYY-MM-DD hh:mm:ss"),
};

const editPostFB = (post_id = null, post = {}) => {
  return function (dispatch, getState, { history }) {
    dispatch(loading(true));
    if (!post_id) {
      console.log("게시물 정보가 없어요!");
      return;
    }

    const _image = getState().image.preview;

    const _post_idx = getState().post.list.findIndex((p) => p.id === post_id);
    const _post = getState().post.list[_post_idx];

    console.log(_post);

    const postDB = firestore.collection("post");

    if (_image === _post.image_url) {
      postDB
        .doc(post_id)
        .update(post)
        .then((doc) => {
          dispatch(editPost(post_id, { ...post }));
          history.replace("/");
        });

      return;
    } else {
      const user_id = getState().user.user.uid;
      const _upload = storage
        .ref(`images/${user_id}_${new Date().getTime()}`)
        .putString(_image, "data_url");

      _upload.then((snapshot) => {
        snapshot.ref
          .getDownloadURL()
          .then((url) => {
            console.log(url);

            return url;
          })
          .then((url) => {
            postDB
              .doc(post_id)
              .update({ ...post, image_url: url })
              .then((doc) => {
                dispatch(editPost(post_id, { ...post, image_url: url }));
                history.replace("/");
              });
          })
          .catch((err) => {
            window.alert("앗! 이미지 업로드에 문제가 있어요!");
            console.log("앗! 이미지 업로드에 문제가 있어요!", err);
          });
      });
    }
  };
};
// firebase에 포스트 추가하는 기능
const addPostFB = (contents = "") => {
  return function (dispatch, getState, { history }) {
    // firebase에 추가를 하려면 추가할데이터의 모양을 알아야한다.
    const postDB = firestore.collection("post");

    const _user = getState().user.user;
    console.log(_user);
    const user_info = {
      user_name: _user.user_name,
      user_id: _user.uid,
      user_profile: _user.user_profile,
    };

    console.log(user_info);
    const _post = {
      ...initialPost,
      // 22번째 initialPost값들이 들어온다.그리고
      contents: contents,
      insert_dt: moment().format("YYYY-MM-DD hh:mm:ss"),
    };

    // getState()로 store의 상태값에 접근할 수 있다.
    const _image = getState().image.preview;
    console.log(typeof _image);
    console.log(_image);

    // 중복된 파일명이 생기지 않게 하기위해서 => 유저의uid와지금시간엮어서 파일명만들기
    const _upload = storage
      .ref(`images/${user_info.user_id}_${new Date().getTime()}`)
      .putString(_image, "data_url");

    _upload.then((snapshot) => {
      snapshot.ref
        .getDownloadURL()
        .then((url) => {
          console.log(url);
          return url;
        })
        .then((url) => {
          //추가하는작업
          postDB
            .add({ ...user_info, ..._post, image_url: url })
            .then((doc) => {
              // 리덕스에서 관리하는 데이터타입과,firestore에 넣는
              // 데이터 모양새가 달라서 모양새 맞춰주는거 까먹지말기
              let post = {
                user_info,
                ..._post,
                id: doc.id,
                image_url: url,
              };
              console.log(post);
              dispatch(addPost(post));
              history.replace("/");
              dispatch(imageActions.setPreview(null));
            })
            .catch((err) => {
              // 에러가 났을때
              window.alert("앗! 포스트 작성에 문제가 있어요!");
              console.log("post 작성 실패!", err);
            });
        })
        .catch((err) => {
          window.alert("앗! 이미지 없로드에 문제가 있어요!");
          console.log("앗! 이미지 업로드에 문제가 있어요!", err);
        });
    });
  };
};
// &1)firebase 사이트에서 post컬렉션만든후
//&2)firebase에서 포스트 가져오기
const getPostFB = (start = null, size = 3) => {
  return function (dispatch, getState, { history }) {
    let _paging = getState().post.paging;

    if (_paging.start && !_paging.next) {
      return;
    }
    // firebase db에 접근하기
    const postDB = firestore.collection("post");

    let query = postDB.orderBy("insert_dt", "desc");

    if (start) {
      query = query.startAt(start);
    }

    query
      .limit(size + 1)
      .get()
      .then((docs) => {
        let post_list = [];

        let paging = {
          start: docs.docs[0],
          next:
            docs.docs.lenght === size + 1
              ? docs.docs[docs.docs.length - 1]
              : null,
          size: size,
        };

        docs.forEach((doc) => {
          // firestore에서 가지고 온 값들을 _post에
          let _post = doc.data();

          //["commenct_cnt", "content", ...](딕셔너리의 키값들로 배열을 만들어주어라)
          let post = Object.keys(_post).reduce(
            (acc, cur) => {
              //   3-3강의
              if (cur.indexOf("user_") !== -1) {
                return {
                  ...acc,
                  user_info: {
                    ...acc.user_info,
                    [cur]: _post[cur],
                  },
                };
              }
              return { ...acc, [cur]: _post[cur] };
            },
            {
              id: doc.id,
              user_info: {},
            }
          );
          post_list.push(post);
        });
        post_list.pop();

        dispatch(setPost(post_list, paging));
      });

    // 그다음 데이터 전부 가져오기
    postDB.get().then((docs) => {
      // 가져온다음
      let post_list = [];

      docs.forEach((doc) => {
        // firestore에서 가지고 온 값들을 _post에
        let _post = doc.data();

        //["commenct_cnt", "content", ...](딕셔너리의 키값들로 배열을 만들어주어라)
        let post = Object.keys(_post).reduce(
          (acc, cur) => {
            //   3-3강의
            if (cur.indexOf("user_") !== -1) {
              return {
                ...acc,
                user_info: {
                  ...acc.user_info,
                  [cur]: _post[cur],
                },
              };
            }
            return { ...acc, [cur]: _post[cur] };
          },
          {
            id: doc.id,
            user_info: {},
          }
        );
        post_list.push(post);
      });
      console.log(post_list);
      dispatch(setPost(post_list));
    });
  };
};

// firestore에서 가져올거임
const getOnePostFB = (id) => {
  return function (dispatch, getState, { history }) {
    //firestore에 있는 데이터 가져올거임

    const postDB = firestore.collection("post");
    postDB
      .doc(id)
      .get()
      .then((doc) => {
        console.log(doc);
        console.log(doc.data());

        let _post = doc.data();
        //["commenct_cnt", "content", ...](딕셔너리의 키값들로 배열을 만들어주어라)
        let post = Object.keys(_post).reduce(
          (acc, cur) => {
            //   3-3강의
            if (cur.indexOf("user_") !== -1) {
              return {
                ...acc,
                user_info: {
                  ...acc.user_info,
                  [cur]: _post[cur],
                },
              };
            }
            return { ...acc, [cur]: _post[cur] };
          },
          {
            id: doc.id,
            user_info: {},
          }
        );
        dispatch(setPost([post]), { start: null, next: null, size: 3 });
      });
  };
};

// 4번순서)리듀서만들기
export default handleActions(
  {
    [SET_POST]: (state, action) =>
      // 어떤처리를 해줄지 적어주기
      produce(state, (draft) => {
        // 위에꺼가지고 어떤처리할지
        // 하나씩들어가게 하려고 ...action
        draft.list.push(...action.payload.post_list);
        draft.paging = action.payload.paging;
        draft.is_loading = false;
      }),

    [ADD_POST]: (state, action) =>
      produce(state, (draft) => {
        // unshift는 배열 맨 앞에 데이터를 넣어줘요!
        draft.list.unshift(action.payload.post);
      }),
    [EDIT_POST]: (state, action) =>
      produce(state, (draft) => {
        let idx = draft.list.findIndex((p) => p.id === action.payload.post_id);

        draft.list[idx] = { ...draft.list[idx], ...action.payload.post };
      }),
    [LOADING]: (state, action) =>
      produce(state, (draft) => {
        draft.is_loading = action.payload.is_loading;
      }),
  },
  initialState
);

// &3)export하기
const actionCreators = {
  setPost,
  addPost,
  editPost,
  getPostFB,
  addPostFB,
  editPostFB,
};

export { actionCreators };
